import React, { useEffect } from "react";
import { useDispatch } from "react-redux";
import { useForm } from "react-hook-form";
import FreeTextQuestion from "./questionType/FreeTextQuestion";
import { TQuestion } from "../../types/exam";
import { answerFreeTextQuestion, startTimer } from "../../redux/actions";

export default function Question({ question }: TProps) {
  const dispatch = useDispatch();
  const { register, handleSubmit, watch, errors } = useForm();

  const { questionId, answerType, questionText, questionTitle } = question;
  useEffect(() => {
    dispatch(startTimer({ questionId }));
  }, []);


  const onSubmit = (data) => {
    let action = undefined;
    switch (answerType) {
      case "freeText":
        const { answerText } = data;
        const payload = { questionId, answerText };
        action = answerFreeTextQuestion(payload);
        break;

      default:
        break;
    }

    dispatch(action);
  };

  const questionBody = () => {
    switch (answerType) {
      case "freeText":
        return (
          <FreeTextQuestion
            register={register}
            handleSubmit={handleSubmit}
            watch={watch}
            errors={errors}
            onSubmit={onSubmit}
          />
        );
      default:
        return <div>Something went wrong</div>;
    }
  };

  return (
    <div>
      <div>{questionTitle}</div>
      <div>{questionText}</div>
      {questionBody()}
    </div>
  );
}

interface TProps {
  question: TQuestion;
}
