import { combineReducers } from "@reduxjs/toolkit";
import produce from "immer";
import {
  ANSWER_FREE_TEXT_QUESTION,
  START_TIMER,
  END_TIMER,
  PREVIOUS_QUESTION,
  NEXT_QUESTION,
} from "./actions";
import { initialAppState, initialExam, initialQuestions } from "./initialState";

function questions(state = initialQuestions, { type, payload }) {
  switch (type) {
    case ANSWER_FREE_TEXT_QUESTION:
      const { questionId, answerText } = payload;
      const nextState = produce(state, (draftState) => {
        let question = draftState.byId[questionId];
        question.answerText = answerText;
        question.timeExpired = true;
      });
      return nextState;

    case START_TIMER:

    default:
      return state;
  }
}

function exams(state = initialExam, { type, payload }) {
  switch (type) {
    default:
      return state;
  }
}

function appState(state = initialAppState, { type, payload }) {
  switch (type) {
    case PREVIOUS_QUESTION:
      return state;
    case NEXT_QUESTION:
      return state;

    default:
      return state;
  }
}

export const rootReducer = combineReducers({
  exams,
  questions,
  appState,
});

export default rootReducer;
