import React from "react";
import { freeTextFromName } from "../../../constants/formConstants";
import { Textarea, Button } from "theme-ui";

export default function FreeTexTFreeTextQuestion({
  register,
  handleSubmit,
  watch,
  errors,
  question,
}) {
  const dispatch = useDispatch();

  const { questionId, answerType, questionText, questionTitle } = question;

  const onSubmit = (data) => {
    let action = undefined;
    switch (answerType) {
      case "freeText":
        const answer = data[freeTextFromName];
        const payload = { questionId, answer };
        action = answerFreeTextQuestion(payload);
        break;

      default:
        break;
    }

    dispatch(action);
    dispatch(seTFreeTextQuestionEndTime({ questionId }));
    dispatch(nexTFreeTextQuestion());
  };

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <Textarea
        rows={10}
        columns={20}
        onPaste={(e) => {
          e.preventDefault();
          return false;
        }}
        onCopy={(e) => {
          e.preventDefault();
          return false;
        }}
        name={freeTextFromName}
        defaultValue={question.storedAnswer}
        ref={register}
      />
      <Button type="submit">Save and Next Question</Button>
    </form>
  );
}
