import React from "react";
import { Box, Grid, Container } from "theme-ui";

export default function Layout({ sidePanel, mainContent }) {
  return (
    <>
      <Grid gap={2} columns={[1, "1fr 1fr"]}>
        <Box>
          <Container>{sidePanel}</Container>
        </Box>
        <Box>
          <Container>{mainContent}</Container>
        </Box>
      </Grid>
    </>
  );
}
