import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";

import { Textarea, Button, Input, Grid } from "theme-ui";
import { TRootState } from "../../../types/examTypes";

export default function MakeFreeText({
  register,
  handleSubmit,
  getValues,
  setCurrentAnswerAction,
  reset,
  questionId,
}) {
  /**
   * React Hooks
   */
  const [makeFreeTextState, setMakeFreeTextState] = useState({
    title: null,
    questionText: null,
    masterAnswer: null,
  });

  /**
   * Redux Hooks
   */

  const dispatch = useDispatch();
  const questionTable = useSelector((state: TRootState) => state.questionTable);
  const questionBodyFreeTextTable = useSelector(
    (state: TRootState) => state.questionBodyFreeTextTable
  );

  /**
   * Form Hook
   */

  useEffect(() => {
    setMakeFreeTextState({
      title: questionTable.byId[questionId].title,
      questionText: questionTable.byId[questionId].title,
      masterAnswer: questionTable.byId[questionId].title,
    });
  }, [questionId]);

