import React, { useState } from "react";
import { Grid, Button, Container } from "theme-ui";

export default function MakeSidePanel() {
  const [activeTab, setActiveTab] = useState(0);
  const [tabs, setTabs] = useState(["questions", "exams"]);

  const makeTabContent = () => {
    switch (tabs[activeTab]) {
      case "questions":
        return <div>questions</div>;
      case "exams":
        return <div>exam</div>;
      default:
        break;
    }
  };

  return (
    <div>
      <Grid columns={[1, 2, 2]} gap={3}>
        {tabs.map((btnName, idx) => {
          return (
            <Button
              onClick={() => {
                setActiveTab(idx);
              }}
              variant={idx === activeTab ? "activeTab" : "mutedTab"}
            >
              {btnName}
            </Button>
          );
        })}
      </Grid>
      <Container variant="blueBorder">{makeTabContent()}</Container>
    </div>
  );
}
