import React from "react";
import { useSelector } from "react-redux";
import Question from "./components/question/Question";
import { TRootState } from "./types/exam";
import Timer from "./components/timer/Timer";

function App() {
  const questions = useSelector((state: TRootState) => state.questions);
  const currentExam = useSelector(
    (state: TRootState) => state.exams.currentExam
  );

  

  return (
    <div className="App">
      <Timer questionId={currentQuestion}></Timer>
      <Question question={questions.byId[currentQuestion]} />
    </div>
  );
}

export default App;
