import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";

import { Textarea, Button, Input, Grid } from "theme-ui";
import { TRootState } from "../../../types/examTypes";

export default function MakeFreeText({
  register,
  handleSubmit,
  getValues,
  setCurrentAnswerAction,
  reset,
}) {
  /**
   * React Hooks
   */

  const [numberOfAnswers, setNumberOfAnswers] = useState(2);

  /**
   * Redux Hooks
   */
  const dispatch = useDispatch();
  const { currentQuestionId } = useSelector(
    (state: TRootState) => state.examState
  );
  const questionTable = useSelector((state: TRootState) => state.questionTable);
  const questionBodyFreeTextTable = useSelector(
    (state: TRootState) => state.questionBodyFreeTextTable
  );

  let question = undefined;
  let questionBody = undefined;

  if (currentQuestionId) {
    question = questionTable.byId[currentQuestionId];
    questionBody = questionBodyFreeTextTable.byId[currentQuestionId];
  }

  /**
   * Form Hook
   */

  const onSubmit = (data) => {
    console.log("submitted");
  };


  /**
   * Functions
   */

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <Input
        ref={register}
        name={"questionTitle"}
        placeholder="Provide a Question Title here"
        defaultValue={question ? question.title : null}
      />
      <Textarea
        rows={5}
        name={"questionText"}
        placeholder={"Provide a question here."}
        ref={register}
        defaultValue={question ? question.text : null}
      />

      {numberOfAnswers.map((possibleAnswer, index) => {
        return (
          <div key={v4()}>
            <Label>
              {/* <Controller as={Checkbox} name={multipleChoiceFormName} /> */}
              <Checkbox
                defaultChecked={false}
                name={`${multipleChoiceFormName}[${index}]`}
                ref={register}
              />
              {possibleAnswer}
            </Label>
          </div>
        );
      })}
      <Button type="submit">Save and Next Question</Button>
      <Button
        sx={{ marginRight: "0.5rem" }}
        variant="warning"
        onClick={() => {
          reset();
        }}
      >
        Reset
      </Button>
      <Button type="submit">Save</Button>
    </form>
  );
}
