import { combineReducers } from "@reduxjs/toolkit";
import { ANSWER_QUESTION, START_TIMER, END_TIMER } from "./actions";
import { initialState } from "./initialState";

export const rootReducer = combineReducers({
  exam,
});

const exam = combineReducers({
  time,
});

const question = combineReducers({
  answer,

})

function answer(state: string = "", { type, payload }) {
  switch (type) {
    case ANSWER_QUESTION:
      return state;

    default:
      return state;
  }
}

function time(state: number = 0, { type, payload }) {
  switch (type) {
    case START_TIMER:
      return state;

    case END_TIMER:
      return state;

    default:
      return state;
  }
}

export default rootReducer;
