import { initialState } from "./initialState";
import { ANSWER_QUESTION, START_TIMER, END_TIMER } from "./actions";
import { combineReducers, Action } from "@reduxjs/toolkit";

export const rootReducer = combineReducers({
  question,
});

export function question(state: string = "", action) {
  switch (action.type) {
    case undefined:
      return initialState;
    case ANSWER_QUESTION:
      return state;

    case START_TIMER:
      return state;

    case END_TIMER:
      return action.answer;

    default:
      return state;
  }
}
