import React, { useState } from "react";
import { Grid, Button } from "theme-ui";

export default function MakeSidePanel() {
  const [activeTab, setActiveTab] = useState(0);
  const [tabs, setTabs] = useState(["questions", "exams"]);

  const makeTabContent = () => {
    switch (tabs[activeTab]) {
      case "questions":
        return <div />;
      case "exams":
        return <div />;
      default:
        break;
    }
  };

  return (
    <div>
      <Grid columns={[1, 2, 2]} gap={3}>
        {tabs.map((btnName, idx) => {
          return (
            <Button variant={idx === activeTab ? "primary" : "secondary"}>
              {btnName}
            </Button>
          );
        })}
      </Grid>
      {makeTabContent()}
    </div>
  );
}
