import React from "react";
import { Box, Grid, Container } from "theme-ui";

export default function Layout({
  sidePanel = undefined,
  mainContent = undefined,
  header = undefined,
}) {
  return (
    <>
      {header && <Container sx={{ variant: "header" }}>{header}</Container>}
      <Grid gap={2} columns={[1, "1fr 1fr"]}>
        {sidePanel && (
          <Box>
            <Container>{sidePanel}</Container>
          </Box>
        )}
        {mainContent && (
          <Container sx={{ variant: "layout.flex" }} bg="muted">
            {mainContent}
          </Container>
        )}
      </Grid>
    </>
  );
}
