import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { store } from "../../index";
import { Heading } from "theme-ui";
import { TRootState } from "../../types/examTypes";
import { nextQuestion } from "../../redux/actions";

export default function Timer({ questionId }) {
  const dispatch = useDispatch();
  const {currentExamId} = useSelector((state:TRootState) => state.examState)
  const answer = useSelector(
    (state: TRootState) => state.answerTable.byId[questionId]
  );
  const question = useSelector(
    (state: TRootState) => state.questionTable.byId[questionId]
  );
  const currentExam = useSelector((state:TRootState) => state.examTable[current])
  const { timeStart } = answer;
  const { timeLimitMs } = question;
  const now = Date.now();

  const [timeLeft, setTimeLeft] = useState(
    (timeStart ? Math.trunc(timeLimitMs - (now - timeStart)) : timeLimitMs) /
      1000
  );

  useEffect(() => {
    const interval = setInterval(() => {
      const now = Date.now();
      const timeStart = store.getState().answerTable.byId[questionId].timeStart;
      const newTimeLeft = Math.trunc((timeLimitMs - (now - timeStart)) / 1000);
      if (newTimeLeft >= 0) {
        setTimeLeft(newTimeLeft);
      } else {
        dispatch(
          nextQuestion({ currentExam: currentExam.byId[currentExamId] })
        );

        clearInterval(interval);
      }
    }, 1000);

    return () => {
      clearInterval(interval);
    };
  }, [question.questionId]);

  return <Heading as={"h3"}>{timeLeft}</Heading>;
}
