        ref={register}
        defaultValue={question ? question.text : null}
      />

      {possibleAnswers.map((possibleAnswer, index) => {
        return (
          <div
            key={v4()}
            style={{ marginTop: "0.5rem", alignContent: "center" }}
          >
            <Label>
              {/* <Controller as={Checkbox} name={multipleChoiceFormName} /> */}
              <Checkbox
