import React from "react";
import { Box, Grid, Card, Container } from "theme-ui";

export default function Layout({
  sidePanel = undefined,
  mainContent = undefined,
  header = undefined,
}) {
  const columns = sidePanel ? [1, "1fr 1fr"] : [1];
  return (
    <Container variant="masterContainer">
      {header && <Card sx={{ variant: "header" }}>{header}</Card>}
      <Grid gap={2} columns={columns}>
        {sidePanel && (
          <Box>
            <Card>{sidePanel}</Card>
          </Box>
        )}
        {mainContent && (
          <Card sx={{ variant: "layout.flex" }} bg="muted">
            {mainContent}
          </Card>
        )}
      </Grid>
    </Container>
  );
}
