import React, { useEffect, useContext, useState } from "react";
import { v4 } from "uuid";
import { useForm } from "react-hook-form";
import { Input, Badge, Heading, Select, Button } from "theme-ui";

import { ExamContext } from "../../../services/examContext";

export default function Exam() {
  const { loadAllUsers, allUsers } = useContext(ExamContext);
  const [newExamState, setNewExamState] = useState({
    addedUsers: [],
    addedExams: [],
  });
  const { register, handleSubmit, watch, errors, reset, getValues } = useForm();

  const addUser = () => {
    const newUserId = getValues().newUser;
    setNewExamState((old) => ({
      ...old,
      addedUsers: [...old.addedExams, newUserId],
    }));
  };

  return (
    <div>
      <Heading as="h2">Create New Exam</Heading>
      <form onSubmit={handleSubmit(() => {})}>
        <Input
          sx={{ marginTop: "1rem" }}
          ref={register}
          name={"questionTitle"}
          placeholder="Provide a Exam Title here"
          defaultValue={""}
        />
        {newExamState.addedUsers.map((el) => {
          return <Badge key={v4()}>{el}</Badge>;
        })}
        <Select name="newUser">
          {allUsers.rows.map((el) => {
            console.log(el);

            return (
              <option key={v4()} value={el}>
                {el.name}
              </option>
            );
          })}
        </Select>
        <Button onClick={addUser}>+</Button>
      </form>
    </div>
  );
}
